mapper.select:
SELECT count(*) as maxPages FROM MITEMTB d WHERE 1 = 1
AND d.ITEM_DIV = #{paramItemDiv }
AND d.ITEM_FG = #{paramItemFg }
AND ( LOWER(d.ITEM_NM) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) OR LOWER(d.ITEM_CD) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) )
ORDER BY d.MAKE_DATE DESC

SELECT ROW_NUMBER() OVER (ORDER BY d.MAKE_DATE desc) as rowNum , d.ITEM_CD as itemCd , d.ITEM_DIV as itemDiv , c1.COMM_NM as itemDivNm , d.ITEM_NM as itemNm , d.AUTHOR_NM as authorNm , d.ITEM_FG as itemFg , c2.COMM_NM as itemFgNm , d.ITEM_CL as itemCl , d.ISBN as isbn , d.I_PRICE as iPrice , d.O_PRICE as oPrice , d.S_PRICE as sPrice , d.P1_PAGE as p1Page , d.P2_PAGE as p2Page , d.EBOOK_YN as ebookYn , d.EBOOK_ISBN as ebookIsbn , d.EBOOK_PRICE as ebookPrice , d.USE_YN as useYn , d.USE_EXPLAIN as useExplain , d.SUN_NM as sunNm , d.COPYRIGHT_NM as copyrightNm , d.COPYRIGHT_SDAY as copyrightSday , d.COPYRIGHT_EDAY as copyrightEday , d.AGENT_CD as agentCd , ag.AGENT_NM as agentNm , d.BUNDLE_ITEM_CD as bundleItemCd , d.MANAGER_NM as managerNm , d.REMARKS as remarks , d.MAKE_DATE as makeDate , d.PUB_DATE as pubDate , d.REG_DATE as regDate , d.REG_ID as regId , d.UPDATE_DATE as updateDate , d.UPDATE_ID as updateId
FROM MITEMTB d left join MAGENTTB ag ON ag.AGENT_CD = d.AGENT_CD left join MCOMMTB c1 ON c1.COMM_CD = d.ITEM_DIV left join MCOMMTB c2 ON c2.COMM_CD = d.ITEM_FG
WHERE 1 = 1
AND d.ITEM_DIV = #{paramItemDiv }
AND d.ITEM_FG = #{paramItemFg }
AND ( LOWER(d.ITEM_NM) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) OR LOWER(d.ITEM_CD) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) )
ORDER BY d.MAKE_DATE DESC LIMIT #{paramPageRows} OFFSET #{paramStartRowNum }

SELECT d.ITEM_CD as itemCd , d.ITEM_CD as oldItemCd , d.ITEM_DIV as itemDiv , c1.COMM_NM as itemDivNm , d.ITEM_NM as itemNm , d.AUTHOR_NM as authorNm , d.ITEM_FG as itemFg , c2.COMM_NM as itemFgNm , d.ITEM_CL as itemCl , d.ISBN as isbn , d.I_PRICE as iPrice , d.O_PRICE as oPrice , d.S_PRICE as sPrice , d.P1_PAGE as p1Page , d.P2_PAGE as p2Page , d.EBOOK_YN as ebookYn , d.EBOOK_ISBN as ebookIsbn , d.EBOOK_PRICE as ebookPrice , d.USE_YN as useYn , d.USE_EXPLAIN as useExplain , d.SUN_NM as sunNm , d.COPYRIGHT_NM as copyrightNm , d.COPYRIGHT_SDAY as copyrightSday , d.COPYRIGHT_EDAY as copyrightEday , d.AGENT_CD as agentCd , ag.AGENT_NM as agentNm , d.BUNDLE_ITEM_CD as bundleItemCd , d.MANAGER_NM as managerNm , d.REMARKS as remarks , d.MAKE_DATE as makeDate , d.PUB_DATE as pubDate , d.REG_DATE as regDate , d.REG_ID as regId , d.UPDATE_DATE as updateDate , d.UPDATE_ID as updateId
, 'U' as iuD
FROM MITEMTB d left join MAGENTTB ag ON ag.AGENT_CD = d.AGENT_CD left join MCOMMTB c1 ON c1.COMM_CD = d.ITEM_DIV left join MCOMMTB c2 ON c2.COMM_CD = d.ITEM_FG WHERE d.ITEM_CD = #{paramItemCd}

select IFNULL(CAST(MAX(ITEM_CD) as unsigned) + 1, '1001') as maxCd
  FROM MITEMTB
  WHERE ITEM_CD  <  8000;

