SELECT count(*) as maxPages FROM MCUSTTB a WHERE 1 = 1 AND a.USE_YN = #{paramUseYn }
AND a.DIV_CD = #{paramDivCd }
AND ( LOWER(a.CUST_NM) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) OR LOWER(a.CUST_BUSIN_NM) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) OR LOWER(a.CUST_CD) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) )
ORDER BY a.MAKE_DATE DESC

SELECT ROW_NUMBER() OVER (ORDER BY a.MAKE_DATE desc) as rowNum , a.CUST_CD as custCd , a.CUST_NM as custNm , a.DIV_CD as divCd , c1.COMM_NM as divCdNm , a.ZONE_NM as zoneNm , a.CUST_BUSIN_NM as custBusinNm , a.CUST_OWNER as custOwner , a.CUST_BUSIN_NO as custBusinNo , a.CUST_COND as custCond , a.CUST_KIND as custKind , a.CUST_ZIP1 as custZip1 , a.CUST_ADDR1 as custAddr1 , a.CUST_ADDR1X as custAddr1X , a.CUST_ZIP2 as custZip2 , a.CUST_ADDR2 as custAddr2 , a.CUST_ADDR2X as custAddr2X , a.MANAGER_NM as managerNm , a.MOBILE as mobile , a.TEL as tel , a.FAX as fax , a.YUL_WT as yulWt , a.YUL_HM as yulHm , a.YUL_MJ as yulMj , a.YUL_NP as yulNp , a.YUL_TB as yulTb , a.YUL_HD as yulHd , a.YUL_HS as yulHs , a.YUL_SG as yulSg , a.YUL_GT as yulGt , a.LIMIT_AMT as limitAmt , a.MAKE_DATE as makeDate , a.BILL_YN as billYn , a.BILL_EMAIL as billEmail , a.USE_YN as useYn , a.USE_EXPLAIN as useExplain , a.REMARKS as remarks , a.REMARKS2 as remarks2 , a.REG_DATE as regDate , a.REG_ID as regId , a.UPDATE_DATE as updateDate , a.UPDATE_ID as updateId
FROM MCUSTTB a left join MCOMMTB c1 ON c1.COMM_CD = a.DIV_CD
WHERE 1 = 1 AND a.USE_YN = #{paramUseYn }
AND a.DIV_CD = #{paramDivCd }
AND ( LOWER(a.CUST_NM) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) OR LOWER(a.CUST_BUSIN_NM) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) OR LOWER(a.CUST_CD) LIKE CONCAT( '%', #{paramSearchValue }, '%' ) )
ORDER BY a.MAKE_DATE DESC LIMIT #{paramPageRows} OFFSET #{paramStartRowNum }

