mapper.select:
SELECT IFNULL( LPAD(CAST(MAX(SEQ) AS UNSIGNED) + 1, 4, '0'), '0001') as maxSeq
 FROM SBUYHTB
WHERE BUY_DAY   =   #{paramBuyDay }

insert.foreach:
INSERT INTO SBUYHTB
    (
          BUY_DAY
        , SEQ
        , CUST_CD
        , DIV_CD
        , REMARKS
        , REG_DATE
        , REG_ID
        , UPDATE_DATE
        , UPDATE_ID
    ) VALUES (
          #{item.buyDay}
        , #{item.seq}
        , #{item.custCd}
        , #{item.divCd}
        , #{item.remarks}
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
    )

update.foreach:
UPDATE SBUYHTB
    SET
          CUST_CD       =   #{item.custCd}
        , DIV_CD        =   #{item.divCdCd}
        , REMARKS       =   #{item.remarks}
        , UPDATE_DATE   =   NOW()
        , UPDATE_ID     =   #{userId}
WHERE BUY_DAY           =   #{item.buyDay}
  AND SEQ               =   #{item.seq}

delete.foreach:
DELETE FROM SBUYHTB
 WHERE BUY_DAY           =   #{item.buyDay}
   AND SEQ               =   #{item.seq}

DELETE FROM SBUYDTB
WHERE BUY_DAY       =   #{item.buyDay}
  AND SEQ           =   #{item.seq}

insert.foreach:
INSERT INTO SBUYDTB
    (
          BUY_DAY
        , SEQ
        , CNT
        , CUST_CD
        , DIV_CD
        , BUY_FG
        , PROD_CD
        , QTY
        , PRICE
        , AMT
        , BUY_YUL
        , REMARKS
        , REG_DATE
        , REG_ID
        , UPDATE_DATE
        , UPDATE_ID
    ) VALUES (
          #{item.buyDay}
        , #{item.seq}
        , #{item.cnt}
        , #{item.custCd}
        , #{item.divCd}
        , #{item.buyFg}
        , #{item.prodCd}
        , #{item.qty}
        , #{item.price}
        , #{item.amt}
        , #{item.buyYul}
        , #{item.remarks}
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
    )

update.foreach:
UPDATE SBUYDTB
    SET
          CUST_CD       =   #{item.custCd}
        , DIV_CD        =   #{item.divCd}
        , BUY_FG        =   #{item.buyFg}
        , PROD_CD       =   #{item.prodCd}
        , QTY           =   #{item.qty}
        , PRICE         =   #{item.price}
        , AMT           =   #{item.amt}
        , BUY_YUL       =   #{item.buyYul}
        , REMARKS       =   #{item.remarks}
        , UPDATE_DATE   =   NOW()
        , UPDATE_ID     =   #{userId}
WHERE BUY_DAY           =   #{item.buyDay}
  AND SEQ               =   #{item.seq}
  AND CNT               =   #{item.cnt}

delete.foreach:
DELETE FROM SBUYDTB
 WHERE BUY_DAY           =   #{item.buyDay}
   AND SEQ               =   #{item.seq}
   AND CNT               =   #{item.cnt}
