mapper.select:
SELECT count(*)
FROM XUSERTB
WHERE USER_ID = #{id}

mapper.insert:
INSERT INTO XUSERTB (
                       USER_ID
                     , PASSWD
                     , USER_NM
                     , ROLE
) VALUES (
          #{user_id}
          , #{passwd}
          , #{userName}
          , #{role}
                 )

INSERT INTO XTOKENTB (
                       USER_ID
                     , ACCESS_TOKEN
                     , REFRESH_TOKEN
) VALUES (
             #{userId}
         , #{accessToken}
         , #{refreshToken}
         )

ON DUPLICATE KEY UPDATE
          ACCESS_TOKEN =  #{accessToken}
        , REFRESH_TOKEN = #{refreshToken}

mapper.select:
/**
Mapper:AuthMapper.getAccessToken
*/
SELECT REFRESH_TOKEN
FROM XTOKENTB
WHERE ACCESS_TOKEN = #{accessToken}

mapper.update:
/**
Mapper:AuthMapper.updateRefreshToken
*/
UPDATE XTOKENTB
SET ACCESS_TOKEN = #{accessToken}
  ,   REFRESH_TOKEN = #{refreshToken}
WHERE USER_ID = #{userId}
