insert.foreach:
INSERT INTO SFEESHTB
    (
          PROD_CD
        , AGENT_CD
        , FEES_SDAY
        , FEES_EDAY
        , FEES_FG
        , CALC_FG
        , COND_FG
        , PM_QTY
        , STOP_YN
        , REMARKS
        , REG_DATE
        , REG_ID
        , UPDATE_DATE
        , UPDATE_ID
    ) VALUES (
          #{item.prodCd}
        , #{item.agentCd}
        , #{item.feesSday}
        , #{item.feesEday}
        , #{item.feesFg}
        , #{item.calcFg}
        , #{item.condFg}
        , #{item.pmQty}
        , #{item.stopYn}
        , #{item.remarks}
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
    )

update.foreach:
UPDATE SFEESHTB
    SET
          AGENT_CD      =   #{item.agentCd}
        , FEES_SDAY     =   #{item.feesSday}
        , FEES_EDAY     =   #{item.feesEday}
        , FEES_EDAY     =   #{item.feesEday}
        , FEES_FG       =   #{item.feesFg}
        , CALC_FG       =   #{item.calcFg}
        , COND_FG       =   #{item.condFg}
        , PM_QTY        =   #{item.pmQty}
        , STOP_YN       =   #{item.stopYn}
        , REMARKS       =   #{item.remarks}
        , UPDATE_DATE   =   NOW()
        , UPDATE_ID     =   #{userId}
    WHERE PROD_CD       =   #{item.prodCd}
      AND AGENT_CD      =   #{item.oldAgentCd}

delete.foreach:
DELETE FROM SFEESHTB
WHERE PROD_CD       =   #{item.prodCd}
  AND AGENT_CD      =   #{item.agentCd}

DELETE FROM SFEESDTB
WHERE PROD_CD       =   #{item.prodCd}
  AND AGENT_CD      =   #{item.agentCd}

insert.foreach:
INSERT INTO SFEESDTB
    (
          PROD_CD
        , AGENT_CD
        , SEQ
        , BASE_QTY
        , BASE_YUL
        , BASE_AMT
        , REMARKS
        , REG_DATE
        , REG_ID
        , UPDATE_DATE
        , UPDATE_ID
    ) VALUES (
          #{item.prodCd}
        , #{item.agentCd}
        , (SELECT IFNULL( MAX(fd.SEQ) + 1, 1) as seq
             FROM SFEESDTB fd
            WHERE fd.PROD_CD   =   #{item.prodCd}
              AND fd.AGENT_CD 	= 	#{item.agentCd}
            )
        , #{item.baseQty}
        , #{item.baseYul}
        , #{item.baseAmt}
        , #{item.remarks}
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
    )

update.foreach:
UPDATE SFEESDTB
    SET
          AGENT_CD      =   #{item.agentCd}
        , BASE_QTY      =   #{item.baseQty}
        , BASE_YUL      =   #{item.baseYul}
        , BASE_AMT      =   #{item.baseAmt}
        , REMARKS       =   #{item.remarks}
        , UPDATE_DATE   =   NOW()
        , UPDATE_ID     =   #{userId}
    WHERE PROD_CD       =   #{item.prodCd}
      AND AGENT_CD      =   #{item.oldAgentCd}
      AND SEQ           =   #{item.seq}

delete.foreach:
DELETE FROM SFEESDTB
WHERE PROD_CD       =   #{item.prodCd}
  AND AGENT_CD      =   #{item.agentCd}
  AND SEQ           =   #{item.seq}

mapper.select:
SELECT a.PROC_YEAR as procYear , a.PROC_MONTH as procMonth , a.PROC_YEAR as oldProcYear , a.PROC_MONTH as oldProcMonth , a.WO_QTY as woQty -- 이월미정산수량 , a.W_QTY as wQty -- 정산누적수량 , a.PM_QTY as pmQty -- 홍보수량 , a.W_AMT as wAmt -- 선인세금액 , a.P_AMT as pAmt -- 종이책중간정산금액(상반기정산액) , a.E_AMT as eAmt -- 전자책정산금액 , a.D_AMT as dAmt -- 소멸액 (인세기간후 남아있는 금액) , a.REMARKS as remarks , a.PROD_CD as prodCd , a.AGENT_CD as agentCd , 'R' as iuD FROM SFEESWTB a
where a.PROD_CD = #{paramProdCd } and a.AGENT_CD = #{paramAgentCd }
order by a.PROC_YEAR asc, PROC_MONTH asc

insert.foreach:
INSERT INTO SFEESWTB (
          PROD_CD
        , AGENT_CD
        , PROC_YEAR
        , PROC_MONTH
        , WO_QTY
        , W_QTY
        , PM_QTY
        , W_AMT
        , P_AMT
        , E_AMT
        , D_AMT
        , REMARKS
        , REG_DATE
        , REG_ID
        , UPDATE_DATE
        , UPDATE_ID
) VALUES (
          #{item.prodCd }
        , #{item.agentCd }
        , #{item.procYear }
        , #{item.procMonth }
        , #{item.woQty }
        , #{item.wQty }
        , #{item.pmQty }
        , #{item.wAmt }
        , #{item.pAmt }
        , #{item.eAmt }
        , #{item.dAmt }
        , #{item.remarks }
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
)

update.foreach:
UPDATE SFEESWTB
    SET
          PROC_YEAR     =   #{item.procYear }
        , PROC_MONTH    =   #{item.procMonth }
        , WO_QTY        =   #{item.woQty }
        , W_QTY         =   #{item.wQty }
        , PM_QTY        =   #{item.pmQty }
        , W_AMT         =   #{item.wAmt }
        , P_AMT         =   #{item.pAmt }
        , E_AMT         =   #{item.eAmt }
        , D_AMT         =   #{item.dAmt }
        , REMARKS       =   #{item.remarks }
        , UPDATE_DATE   =   NOW()
        , UPDATE_ID     =   #{userId}
WHERE PROD_CD       =   #{item.prodCd}
  AND AGENT_CD      =   #{item.agentCd}
  AND PROC_YEAR     =  #{item.oldProcYear }
  AND PROC_MONTH    =  #{item.oldProcMonth }

delete.foreach:
DELETE FROM SFEESWTB
WHERE PROD_CD       =   #{item.prodCd}
  AND AGENT_CD      =   #{item.agentCd}
  AND PROC_YEAR     =   #{item.procYear }
  AND PROC_MONTH    =   #{item.procMonth }

insert.foreach:
INSERT INTO SFEESJTB (
          PROC_YEAR
        , PROC_MONTH
        , AGENT_CD
        , PROD_CD
        , J_AMT
        , REMARKS
        , REG_DATE
        , REG_ID
        , UPDATE_DATE
        , UPDATE_ID
) VALUES (
          #{item.procYear }
        , #{item.procMonth }
        , #{item.agentCd }
        , #{item.prodCd }
        , #{item.jAmt }
        , #{item.remarks }
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
)

update.foreach:
UPDATE SFEESJTB
    SET
          AGENT_CD      =   #{item.agentCd }
        , PROD_CD       =   #{item.prodCd }
        , J_AMT         =   #{item.jAmt }
        , REMARKS       =   #{item.remarks }
        , UPDATE_DATE   =   NOW()
        , UPDATE_ID     =   #{userId}
WHERE PROC_YEAR     =   #{item.procYear }
  AND PROC_MONTH    =   #{item.procMonth }
  AND AGENT_CD      =   #{item.agentCdOld}
  AND PROD_CD       =   #{item.prodCdOld}

delete.foreach:
DELETE FROM SFEESJTB
     WHERE PROC_YEAR    =   #{item.procYear }
      AND PROC_MONTH    =   #{item.procMonth }
      AND AGENT_CD      =   #{item.agentCd}
      AND PROD_CD       =   #{item.prodCd}
