mapper.select:
SELECT ROWNUM() as rowNum , id.BUY_DAY as buyDay , id.SEQ as seq , id.CNT as cnt , id.CUST_CD as custCd , cu.CUST_NM as custNm , id.DIV_CD as divCd , id.BUY_FG as buyFg , c1.COMM_NM as buyFgNm , id.PROD_CD as prodCd , pd.PROD_NM as prodNm , SUM(id.QTY) as qty , id.PRICE as price , SUM(id.AMT) as amt , id.BUY_YUL as buyYul , id.REMARKS as remarks FROM SBUYDTB as id -- 거래처코드 left outer join MCUSTTB as cu ON cu.CUST_CD = id.CUST_CD left outer join MPRODTB as pd ON pd.PROD_CD = id.PROD_CD left outer join MCOMMTB as c1 ON c1.COMM_CD = id.BUY_FG
where 1=1 AND id.BUY_DAY BETWEEN #{paramBuyDayFrom } AND #{paramBuyDayTo }
AND id.BUY_FG = #{paramBuyFg }
AND ( cu.CUST_CD LIKE CONCAT( '%', #{paramValue }, '%' ) OR id.REMARKS LIKE CONCAT( '%', #{paramValue }, '%') OR cu.CUST_NM LIKE CONCAT( '%', #{paramValue }, '%') OR id.PROD_CD LIKE CONCAT( '%', #{paramValue }, '%') OR pd.PROD_NM LIKE CONCAT( '%', #{paramValue }, '%') )
GROUP BY id.BUY_DAY, id.SEQ, id.CNT WITH ROLLUP

SELECT h.DEAL_DAY as dealDay , h.SEQ as seq , h.DIV_CD as divCd , h.CUST_CD as custCd , c.CUST_NM as custNm , h.CUSTS_CD as custsCd , h.CUST_SEQ as custSeq , h.REMARKS as remarks , h.COUP_RETURN_DAY as coupReturnDay , h.REG_DATE as regDate , h.REG_ID as regId , h.UPDATE_DATE as updateDate , h.UPDATE_ID as updateId
, c.YUL_WT as yulWt , c.YUL_MJ as yulMj , c.YUL_HM as yulHm , c.YUL_NP as yulNp , c.YUL_TB as yulTb , c.YUL_HD as yulHd , c.YUL_HS as yulHs , c.YUL_SG as yulSg , c.YUL_GT as yulGt FROM SDEALHTB as h -- 거래처코드 left outer join MCUSTTB as c ON c.CUST_CD = h.CUST_CD
where 1=1 AND h.DEAL_DAY = #{paramDealDay } AND h.SEQ = #{paramSeq }

SELECT IFNULL( LPAD(CAST(MAX(SEQ) AS UNSIGNED) + 1, 4, '0'), '0001') as maxSeq
FROM SDEALHTB
WHERE DEAL_DAY   =   #{paramDealDay }

SELECT
    COUNT(*) + 1    as  custSeq
  FROM SDEALHTB
 WHERE DEAL_DAY     =   #{paramDealDay }
   AND CUST_CD      =   #{paramCustCd }
   AND ifnull(CUSTS_CD,'')    =   ifnull(#{paramCustsCd },'')

