mapper.select:
SELECT count(*) as maxPages , SUM(QTY) as sumQty , SUM(AMT) as sumAmt FROM SDEALDTB dd -- 도서코드 left outer join SDEALHTB as dh ON dh.DEAL_DAY = dd.DEAL_DAY AND dh.SEQ = dd.SEQ left outer join MCUSTTB as mc ON mc.CUST_CD = dh.CUST_CD left outer join MPRODTB as mp ON mp.PROD_CD = dd.PROD_CD left outer join MCOMMTB as c1 ON c1.COMM_CD = dd.DEAL_FG
WHERE 1 = 1 AND dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND dd.DEAL_FG = #{paramDealFg }
AND dh.CUST_CD = #{paramCustCd }
AND ( dh.CUST_CD LIKE CONCAT( '%', #{paramValue }, '%' ) OR mc.CUST_NM LIKE CONCAT( '%', #{paramValue }, '%') OR dd.REMARKS LIKE CONCAT( '%', #{paramValue }, '%') OR mp.PROD_NM LIKE CONCAT( '%', #{paramValue }, '%') )
ORDER BY dd.DEAL_DAY, dd.SEQ, dd.CNT

SELECT ROW_NUMBER() OVER (ORDER BY dd.DEAL_DAY, dd.SEQ, dd.CNT) as rowNum , dd.DEAL_DAY as dealDay , dd.SEQ as seq , dd.CNT as cnt , dh.CUST_CD as custCd , mc.CUST_NM as custNm , dd.CUSTS_CD as custsCd , dd.DIV_CD as divCd , dd.CUST_SEQ as custSeq , dd.DEAL_FG as dealFg , c1.COMM_NM as dealFgNm , dd.PROD_CD as prodCd , mp.PROD_NM as prodNm , dd.QTY as qty , dd.PRICE as price , dd.AMT as amt , dd.YUL as yul , dd.RETURN_DAY as returnDay , dd.RECEIPT_YN as receiptYn , dd.RECEIPT_DATE as receiptDate , dd.REMARKS as remarks , dd.REG_DATE as regDate , dd.REG_ID as regId , dd.UPDATE_DATE as updateDate , dd.UPDATE_ID as updateId
FROM SDEALDTB as dd -- 도서코드 left outer join SDEALHTB as dh ON dh.DEAL_DAY = dd.DEAL_DAY AND dh.SEQ = dd.SEQ left outer join MCUSTTB as mc ON mc.CUST_CD = dh.CUST_CD left outer join MPRODTB as mp ON mp.PROD_CD = dd.PROD_CD left outer join MCOMMTB as c1 ON c1.COMM_CD = dd.DEAL_FG
where 1=1 AND dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND dd.DEAL_FG = #{paramDealFg }
AND dh.CUST_CD = #{paramCustCd }
AND ( dh.CUST_CD LIKE CONCAT( '%', #{paramValue }, '%' ) OR mc.CUST_NM LIKE CONCAT( '%', #{paramValue }, '%') OR dd.REMARKS LIKE CONCAT( '%', #{paramValue }, '%') OR mp.PROD_NM LIKE CONCAT( '%', #{paramValue }, '%') )
ORDER BY dd.DEAL_DAY, dd.SEQ, dd.CNT LIMIT #{paramPageRows} OFFSET #{paramStartRowNum }

SELECT ROW_NUMBER() OVER (ORDER BY dd.DEAL_DAY, dd.SEQ ) as rowNum , dd.DEAL_DAY as dealDay , dd.SEQ as seq , dd.CUST_CD as custCd , mc.CUST_NM as custNm , count(dd.PROD_CD) as cnt , sum(dd.QTY) as sumQty , sum(dd.AMT) as sumAmt , '' as remarks
FROM SDEALDTB dd left outer join MCUSTTB mc ON mc.CUST_CD = dd.CUST_CD WHERE dd.DEAL_DAY between #{paramPeriodFrom } AND #{paramPeriodTo } AND dd.DIV_CD = '1' -- 출고전표
AND dd.CUST_CD = #{paramCustCd }
AND dd.PROD_CD = #{paramProdCd }
GROUP BY dd.DEAL_DAY, dd.SEQ ORDER BY dd.DEAL_DAY, dd.SEQ

SELECT count(*) as maxPages , SUM(QTY) as sumQty , SUM(AMT) as sumAmt FROM SDEALDTB dd -- 헤더 left outer join SDEALHTB as dh ON dh.DEAL_DAY = dd.DEAL_DAY AND dh.SEQ = dd.SEQ -- 거래처코드 left outer join MCUSTTB as mc ON mc.CUST_CD = dh.CUST_CD -- 도서코드 left outer join MPRODTB mp ON mp.PROD_CD = dd.PROD_CD -- 공통코드 (출고구분) left outer join MCOMMTB as c1 ON c1.COMM_CD = dd.DEAL_FG
WHERE dd.DIV_CD = '9' AND dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND dd.DEAL_FG = #{paramDealFg }
AND dh.CUST_CD = #{paramCustCd }
AND dd.PROD_CD = #{paramProdCd }
AND ( dh.CUST_CD LIKE CONCAT( '%', #{paramValue }, '%' ) OR mc.CUST_NM LIKE CONCAT( '%', #{paramValue }, '%') OR dd.REMARKS LIKE CONCAT( '%', #{paramValue }, '%') OR dd.PROD_CD LIKE CONCAT( '%', #{paramValue }, '%') OR mp.PROD_NM LIKE CONCAT( '%', #{paramValue }, '%') )
ORDER BY dd.DEAL_DAY, dd.SEQ, dd.CNT

SELECT ROW_NUMBER() OVER (ORDER BY dd.DEAL_DAY, dd.SEQ, dd.CNT) as rowNum , dd.DEAL_DAY as dealDay , dd.SEQ as seq , dd.CNT as cnt , dh.CUST_CD as custCd , mc.CUST_NM as custNm , dd.PROD_CD as prodCd , mp.PROD_NM as prodNm , dd.DEAL_FG as dealFg , c1.COMM_NM as dealFgNm , dd.QTY as qty , dd.PRICE as price , dd.AMT as amt , dd.YUL as yul , dd.CUST_SEQ as custSeq , dd.CUSTS_CD as custsCd , dd.REMARKS as remarks , dd.RETURN_DAY as returnDay FROM SDEALDTB dd -- 헤더 left outer join SDEALHTB as dh ON dh.DEAL_DAY = dd.DEAL_DAY AND dh.SEQ = dd.SEQ -- 거래처코드 left outer join MCUSTTB as mc ON mc.CUST_CD = dh.CUST_CD -- 도서코드 left outer join MPRODTB mp ON mp.PROD_CD = dd.PROD_CD -- 공통코드 (출고구분) left outer join MCOMMTB as c1 ON c1.COMM_CD = dd.DEAL_FG
WHERE dd.DIV_CD = '9' AND dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND dd.DEAL_FG = #{paramDealFg }
AND dh.CUST_CD = #{paramCustCd }
AND dd.PROD_CD = #{paramProdCd }
AND ( dh.CUST_CD LIKE CONCAT( '%', #{paramValue }, '%' ) OR mc.CUST_NM LIKE CONCAT( '%', #{paramValue }, '%') OR dd.REMARKS LIKE CONCAT( '%', #{paramValue }, '%') OR dd.PROD_CD LIKE CONCAT( '%', #{paramValue }, '%') OR mp.PROD_NM LIKE CONCAT( '%', #{paramValue }, '%') )
ORDER BY dd.DEAL_DAY, dd.SEQ, dd.CNT LIMIT #{paramPageRows} OFFSET #{paramStartRowNum }

SELECT ROW_NUMBER() OVER (ORDER BY dd.DEAL_DAY, dd.SEQ) as rowNum , dd.DEAL_DAY as dealDay , dd.SEQ as seq , dh.CUST_CD as custCd , mc.CUST_NM as custNm , count(dd.PROD_CD) as prodCnt , SUM(dd.qty) as sumQty , SUM(dd.AMT) as sumAmt , dd.RETURN_DAY as returnDay FROM SDEALDTB dd -- 헤더 left outer join SDEALHTB as dh ON dh.DEAL_DAY = dd.DEAL_DAY AND dh.SEQ = dd.SEQ -- 거래처코드 left outer join MCUSTTB as mc ON mc.CUST_CD = dh.CUST_CD
WHERE dd.DIV_CD = '9' AND dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND dh.CUST_CD = #{paramCustCd }
AND dd.PROD_CD = #{paramProdCd }
GROUP BY dd.DEAL_DAY, dd.SEQ

SELECT a.cust_cd as custCd , mc.CUST_NM as custNm , sum(a.i_qty) as iQty , sum(a.o_qty) as oQty , sum(a.oj_qty) as ojQty , sum(a.ob_qty) as obQty , sum(a.ox_qty) as oxQty , sum(a.oz_qty) as ozQty , sum(a.o_amt) as oAmt , sum(a.ob_amt) as obAmt FROM ( SELECT bd.cust_cd as cust_cd , ifnull(bd.QTY, 0) as i_qty , 0 as o_qty , 0 as oj_qty , 0 as ob_qty , 0 as ox_qty , 0 as oz_qty , 0 as o_amt , 0 as ob_amt FROM SBUYDTB bd
WHERE bd.BUY_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo } AND bd.QTY != 0
AND bd.CUST_CD = #{paramCustCd }
UNION ALL SELECT dd.cust_cd as cust_cd , 0 as i_qty , if(dd.DIV_CD='1', if(substring(dd.DEAL_FG,4,2) = '11', ifnull(dd.QTY, 0), 0),0) as o_qty , if(dd.DIV_CD='1', if(dd.DEAL_FG = '4021205', ifnull(dd.QTY, 0), 0),0) as oj_qty , if(dd.DIV_CD='9' AND dd.DEAL_FG != '4021980', ifnull(dd.QTY, 0),0) as ob_qty , if(dd.DIV_CD='1', if(dd.DEAL_FG = '4021990', ifnull(dd.QTY, 0), 0),0) as ox_qty , if(dd.DEAL_FG='4021980', if(dd.DIV_CD='1', ifnull(dd.QTY, 0),0-ifnull(dd.QTY, 0)),0) as oz_qty , if(dd.DIV_CD='1', if(substring(dd.DEAL_FG,4,2) = '11', ifnull(dd.AMT, 0), 0),0) as o_amt , if(dd.DIV_CD='9' AND dd.DEAL_FG != '4021980', ifnull(dd.AMT, 0),0) as ob_amt FROM SDEALDTB dd
WHERE dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo } AND NOT(dd.QTY=0 AND dd.AMT=0)
AND dd.CUST_CD = #{paramCustCd }
) a left join MCUSTTB mc ON mc.CUST_CD = a.cust_cd GROUP BY a.cust_cd ORDER BY a.cust_cd

SELECT a.prod_cd as prodCd , mp.PROD_NM as prodNm , sum(a.i_qty) as iQty , sum(a.o_qty) as oQty , sum(a.oj_qty) as ojQty , sum(a.ob_qty) as obQty , sum(a.ox_qty) as oxQty , sum(a.oz_qty) as ozQty , sum(a.o_amt) as oAmt , sum(a.ob_amt) as obAmt FROM ( SELECT bd.PROD_CD as prod_cd , ifnull(bd.QTY, 0) as i_qty , 0 as o_qty , 0 as oj_qty , 0 as ob_qty , 0 as ox_qty , 0 as oz_qty , 0 as o_amt , 0 as ob_amt FROM SBUYDTB bd
WHERE bd.BUY_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo } AND bd.CUST_CD = #{paramCustCd } AND bd.QTY != 0 UNION ALL SELECT dd.PROD_CD as prod_cd , 0 as i_qty , if(dd.div_cd='1', if(substring(dd.DEAL_FG,4,2) = '11', ifnull(dd.QTY, 0), 0),0) as o_qty , if(dd.div_cd='1', if(dd.DEAL_FG = '4021205', ifnull(dd.QTY, 0), 0),0) as oj_qty , if(dd.div_cd='9' AND dd.DEAL_FG != '4021980', ifnull(dd.QTY, 0),0) as ob_qty , if(dd.div_cd='1', if(dd.DEAL_FG = '4021990', ifnull(dd.QTY, 0), 0),0) as ox_qty , if(dd.DEAL_FG='4021980', if(dd.div_cd='1', ifnull(dd.QTY, 0),0-ifnull(dd.QTY, 0)),0) as oz_qty , if(dd.div_cd='1', if(substring(dd.DEAL_FG,4,2) = '11', ifnull(dd.AMT, 0), 0),0) as o_amt , if(dd.div_cd='9' AND dd.DEAL_FG != '4021980', ifnull(dd.AMT, 0),0) as ob_amt FROM SDEALDTB dd
WHERE dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo } AND dd.CUST_CD = #{paramCustCd } AND NOT(dd.QTY=0 AND dd.AMT=0)
AND dd.PROD_CD = #{paramProdCd }
) a left join MPRODTB mp ON mp.PROD_CD = a.prod_cd GROUP BY a.prod_cd ORDER BY a.prod_cd

