    ) VALUES (
          #{item.accDay}
        , #{item.seq}
        , #{item.custCd}
        , #{item.accFg}
        , #{item.amt}
        , #{item.remarks}
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
    )

