    SET
          ACC_DAY       =   #{item.accDay}
        , CUST_CD       =   #{item.custCd}
        , ACC_FG        =   #{item.accFg}
        , AMT           =   #{item.amt}
        , REMARKS       =   #{item.remarks}
        , UPDATE_DATE   =   NOW()
        , UPDATE_ID     =   #{userId}
    WHERE ACC_DAY       =   #{item.oldAccDay}
      AND SEQ           =   #{item.oldSeq}

delete.foreach:
DELETE FROM SACCTB
  WHERE ACC_DAY         =   #{item.accDay}
    AND SEQ             =   #{item.seq}

insert.foreach:
INSERT INTO SBILLTB    (
      BILL_SEQ
    , BILL_NO
    , DIV_CD
    , PUB_COMP
    , PUB_NM
    , PUB_DAY
    , REC_DAY
    , PAY_DAY
    , PAY_BANK
    , PAY_POINT
    , PAY_COMP
    , END_DAY
    , BILL_AMT
    , YUL
    , YUL_AMT
    , REMARKS
    , ACC_DAY
    , SEQ
    , REG_DATE
    , REG_ID
    , UPDATE_DATE
    , UPDATE_ID
) VALUES (
      (SELECT IFNULL(MAX(b.BILL_SEQ) + 1, 1) FROM SBILLTB b)
    , #{item.billNo }
    , #{item.divCd }
    , #{item.pubComp }
    , #{item.pubNm }
    , #{item.pubDay }
    , #{item.recDay }
    , #{item.payDay }
    , #{item.payBank }
    , #{item.payPoint }
    , #{item.payComp }
    , #{item.endDay }
    , #{item.billAmt }
    , #{item.yul }
    , #{item.yulAmt }
    , #{item.remarks }
    , #{item.accDay }
    , #{item.seq }
    , now()
    , #{userId}
    , now()
    , #{userId}
)

update.foreach:
UPDATE SBILLTB
    SET
          BILL_NO        =   #{item.billNo }
        , DIV_CD         =   #{item.divCd }
        , PUB_COMP       =   #{item.pubComp }
        , PUB_NM         =   #{item.pubNm }
        , PUB_DAY        =   #{item.pubDay }
        , REC_DAY        =   #{item.recDay }
        , PAY_DAY        =   #{item.payDay }
        , PAY_BANK       =   #{item.payBank }
        , PAY_POINT      =   #{item.payPoint }
        , PAY_COMP       =   #{item.payComp }
        , END_DAY        =   #{item.endDay }
        , BILL_AMT       =   #{item.billAmt }
        , YUL            =   #{item.yul }
        , YUL_AMT        =   #{item.yulAmt }
        , REMARKS        =   #{item.remarks }
        , ACC_DAY        =   #{item.accDay }
        , SEQ            =   #{item.seq }
        , UPDATE_DATE    =   now()
        , UPDATE_ID      =   #{userid}

WHERE BILL_SEQ      =   #{item.billSeq }

mapper.select:
SET @w_amt := 0;
SELECT a.sale_day AS saleDay , IFNULL(a.o_qty, 0) AS oQty , IFNULL(a.o_amt, 0) AS oAmt , IFNULL(a.oj_qty, 0) AS ojQty , IFNULL(a.oj_amt, 0) AS ojAmt , IFNULL(a.ob_qty, 0) AS obQty , IFNULL(a.ob_amt, 0) AS obAmt , IFNULL(a.in_amt, 0) AS inAmt , @w_amt := @w_amt + IFNULL(a.o_amt, 0) - IFNULL(a.ob_amt, 0) - IFNULL(a.in_amt, 0) AS jAmt , '' AS remarks FROM ( -- 과거 데이터 집계 SELECT CONCAT(#{paramYear}, '00') AS sale_day , 0 AS o_qty , 0 AS o_amt , 0 AS oj_qty , 0 AS oj_amt , 0 AS ob_qty , 0 AS ob_amt , 0 AS in_amt , @w_amt := IFNULL(SUM(O_AMT), 0) - IFNULL(SUM(OB_AMT), 0) - IFNULL(SUM(IN_AMT), 0) AS initial_j_amt FROM STSALETB WHERE CUST_CD = #{paramCustCd } AND LEFT(SALE_DAY, 4) < #{paramYear}
UNION ALL
-- 현재 월 데이터 SELECT LEFT(SALE_DAY, 6) AS sale_day , IFNULL(SUM(O_QTY), 0) AS o_qty , IFNULL(SUM(O_AMT), 0) AS o_amt , IFNULL(SUM(OJ_QTY), 0) AS oj_qty , IFNULL(SUM(OJ_AMT), 0) AS oj_amt , IFNULL(SUM(OB_QTY), 0) AS ob_qty , IFNULL(SUM(OB_AMT), 0) AS ob_amt , IFNULL(SUM(IN_AMT), 0) AS in_amt , NULL AS initial_j_amt -- 초기값은 필요 없음 FROM STSALETB WHERE CUST_CD = #{paramCustCd } AND LEFT(SALE_DAY, 4) = #{paramYear} GROUP BY LEFT(SALE_DAY, 6) ) a ORDER BY a.sale_day

SELECT ROW_NUMBER() OVER (ORDER BY ac.ACC_DAY) as rowNum , ac.ACC_DAY as accDay , ac.CUST_CD as custCd , mc.CUST_NM as custNm , (SELECT ((sum(ts.O_AMT) - sum(ts.OB_AMT)) - sum(ts.IN_AMT)) FROM STSALETB ts WHERE ts.SALE_DAY <= ac.ACC_DAY AND ts.CUST_CD = ac.CUST_CD GROUP by ts.CUST_CD ) as wAmt , ac.AMT as amt , CASE when ac.ACC_FG = '1' then '현금' when ac.ACC_FG = '2' then '어음' when ac.ACC_FG = '3' then '은행' when ac.ACC_FG = '4' then '카드' when ac.ACC_FG = '5' then '공제' when ac.ACC_FG = '9' then '기타' else '오류' END as accFgNm , ac.REMARKS as remarks , bl.END_DAY as endDay , bl.DIV_CD as divCd , c1.COMM_NM as divCdNm , bl.PUB_COMP as pubComp , bl.BILL_AMT as billAmt
FROM SACCTB ac
-- 거래처코드 left outer join MCUSTTB as mc ON mc.CUST_CD = ac.CUST_CD -- 어음정보 left outer join SBILLTB as bl ON bl.ACC_DAY = ac.ACC_DAY AND bl.SEQ = ac.SEQ -- 공통코드(어음유형 1303) left outer join MCOMMTB as c1 ON c1.COMM_CD = bl.DIV_CD
WHERE ac.ACC_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND ac.ACC_FG = #{paramAccFg }
AND ac.CUST_CD = #{paramCustCd }
AND ( ac.CUST_CD LIKE CONCAT( '%', #{paramValue }, '%' ) OR mc.CUST_NM LIKE CONCAT( '%', #{paramValue }, '%') OR ac.REMARKS LIKE CONCAT( '%', #{paramValue }, '%') )
ORDER BY ac.ACC_DAY

SELECT bi.BILL_NO as billNo , bi.DIV_CD as divCd , c1.COMM_NM as divCdNm , bi.PUB_COMP as pubComp , bi.PUB_NM as pubNm , bi.PUB_Day as pubDay , bi.REC_DAY as recDay , bi.PAY_DAY as payDay , bi.PAY_POINT as payPoint , bi.PAY_COMP as payComp , bi.END_DAY as endDay , bi.BILL_AMT as billAmt , bi.YUL as yul , bi.YUL_AMT as yulAmt , bi.REMARKS as remarks , bi.ACC_DAY as accDay , bi.SEQ as seq FROM SBILLTB bi -- 어음유형 (공통코드 1303) left outer join MCOMMTB as c1 ON c1.COMM_CD = bi.DIV_CD
WHERE 1=1 AND bi.BILL_AMT != 0
AND bi.DIV_CD = #{paramDivCd }
AND bi.ACC_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND bi.END_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
AND ( bi.PUB_COMP LIKE CONCAT( '%', #{paramValue }, '%' ) OR bi.PUB_NM LIKE CONCAT( '%', #{paramValue }, '%') OR bi.REMARKS LIKE CONCAT( '%', #{paramValue }, '%') )
ORDER BY bi.ACC_DAY DESC

SET @w_qty:=0, @wb_qty:=0;
SELECT a.STOCK_DAY as stockDay , ifnull(a.i_qty,0) as iQty , ifnull(a.i_amt,0) as iAmt , ifnull(a.ib_qty,0) as ibQty , ifnull(a.ib_amt,0) as ibAmt , ifnull(a.o_qty,0) as oQty , ifnull(a.o_amt,0) as oAmt , ifnull(a.oj_qty,0) as ojQty , ifnull(a.oj_amt,0) as ojAmt , ifnull(a.ob_qty,0) as obQty , ifnull(a.ob_amt,0) as obAmt , ifnull(a.ox_qty,0) as oxQty , ifnull(a.ox_amt,0) as oxAmt , ifnull(a.oz_qty,0) as ozQty , ifnull(a.oz_amt,0) as ozAmt , ifnull(a.o_qty,0) + ifnull(a.oj_qty,0) + ifnull(a.ox_qty,0) as otQty , @w_qty := @w_qty + ifnull(a.i_qty,0) - (ifnull(a.o_qty,0)+ifnull(a.oj_qty,0)+ifnull(a.ox_qty,0)+ifnull(a.oz_qty,0)) + ifnull(a.ob_qty,0) as jQty FROM ( SELECT concat(#{paramYear }, "00") as STOCK_DAY , 0 as i_qty , 0 as i_amt , 0 as ib_qty , 0 as ib_amt , 0 as o_qty , 0 as o_amt , 0 as oj_qty , 0 as oj_amt , 0 as ob_qty , 0 as ob_amt , 0 as ox_qty , 0 as ox_amt , 0 as oz_qty , 0 as oz_amt , 0 as ot_qty , @w_qty := @w_qty + ifnull(sum(i_qty),0) - (ifnull(sum(o_qty),0)+ifnull(sum(oj_qty),0)+ifnull(sum(ox_qty),0)+ifnull(sum(oz_qty),0)) + ifnull(sum(ob_qty),0) as j_qty FROM SSTOCKTB
WHERE PROD_CD = #{paramProdCd } AND left(STOCK_DAY, 4) < #{paramYear }
UNION ALL
SELECT left(STOCK_DAY,6) as STOCK_DAY , ifnull(sum(i_qty), 0) as i_qty , ifnull(sum(i_amt), 0) as i_amt , ifnull(sum(ib_qty), 0) as ib_qty , ifnull(sum(ib_amt), 0) as ib_amt , ifnull(sum(o_qty), 0) as o_qty , ifnull(sum(o_amt), 0) as o_amt , ifnull(sum(oj_qty), 0) as oj_qty , ifnull(sum(oj_amt), 0) as oj_amt , ifnull(sum(ob_qty), 0) as ob_qty , ifnull(sum(ob_amt), 0) as ob_amt , ifnull(sum(ox_qty), 0) as ox_qty , ifnull(sum(ox_amt), 0) as ox_amt , ifnull(sum(oz_qty), 0) as oz_qty , ifnull(sum(oz_amt), 0) as oz_amt , 0 as jb_qty , 0 as j_qty FROM SSTOCKTB WHERE PROD_CD = #{paramProdCd } AND left(STOCK_DAY, 4) = #{paramYear } GROUP BY left(STOCK_DAY, 6) ORDER BY STOCK_DAY
) a

drop TEMPORARY TABLE if exists TMP_WORK_TB;
CREATE TEMPORARY TABLE IF NOT EXISTS TMP_WORK_TB
SELECT @rowNum := @rowNum + 1 as row_no , a.sale_ym , a.SALE_DAY , a.SEQ , a.io_div , a.PROD_CD , a.PROD_NM , CUSTS_CD , DIV_CD , a.fg , a.fg_nm , a.cnt , a.o_qty , a.o_amt , a.b_qty , a.b_amt , a.price , a.yul , a.income_amt , a.REMARKS FROM ( SELECT left(dd.DEAL_DAY,6) as sale_ym , dd.DEAL_DAY as SALE_DAY , dd.SEQ as SEQ , '1' as io_div , dd.PROD_CD as PROD_CD , mp.PROD_NM as PROD_NM , dd.CUSTS_CD as CUSTS_CD , dd.DIV_CD as DIV_CD , dd.DEAL_FG as fg , c1.COMM_NM as fg_nm , count(*)-1 as cnt , if(dd.DIV_CD = '1', sum(ifnull(dd.QTY, 0)), 0) as o_qty , if(dd.DIV_CD = '1', sum(ifnull(dd.AMT, 0)), 0) as o_amt , if(dd.DIV_CD = '9', sum(ifnull(dd.QTY, 0)), 0) as b_qty , if(dd.DIV_CD = '9', sum(ifnull(dd.AMT, 0)), 0) as b_amt , dd.PRICE as price , dd.YUL as yul , 0 as income_amt , dd.REMARKS as REMARKS FROM SDEALDTB dd left outer join MPRODTB as mp on mp.PROD_CD = dd.PROD_CD left outer join MCOMMTB as c1 ON c1.COMM_CD = dd.DEAL_FG
WHERE dd.CUST_CD = #{paramCustCd } AND dd.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo } AND dd.DEAL_FG != '1980' GROUP BY dd.DEAL_DAY, CUSTS_CD, dd.SEQ
UNION ALL
SELECT left(ac.acc_date,6) as sale_ym , ac.acc_date as SALE_DAY , ac.seq_cnt as SEQ , '2' as io_div , '' as PROD_CD , case when acc_fg = '1' then '입금(현금)' when acc_fg = '2' then '입금(어음)' when acc_fg = '3' then '입금(은행)' when acc_fg = '4' then '입금(카드)' when acc_fg = '5' then '입금(공제)' else '기타' end as PROD_NM , '' as CUSTS_CD , '' as DIV_CD , '' as fg , '' as fg_nm , 0 as cnt , 0 as o_qty , 0 as o_amt , 0 as b_qty , 0 as b_amt , 0 as price , 0 as yul , ac.acc_amt as income_amt , ac.REMARKS as REMARKS FROM db_sejong.SACCTB ac WHERE ac.CUST_CD = #{paramCustCd } AND ac.acc_date BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo } ) a, (select @rowNum := 0) x ORDER BY a.SALE_DAY, a.io_div, a.SEQ;
-- 출력 부분 SELECT b.row_no as rowNum , b.sale_ym as saleYm , b.SALE_DAY as saleDay , b.SEQ as seq , b.io_div as io , b.PROD_CD as prodCd , (case when b.SALE_DAY = '00000000' then '[ 전월이월 ]' when b.sale_ym is null then '[ 총 계 ]' when b.row_no is null then '[ 월 계 ]' else b.PROD_NM end) as prodNm , b.CUSTS_CD as custsCd , b.DIV_CD as divCd , b.fg as fg , b.fg_nm as fgNm , b.cnt as cnt -- 외종 , b.o_qty as oQty , b.o_amt as oAmt , b.b_qty as bQty , b.b_amt as bAmt , b.price as price , b.yul as yul , b.income_amt as accAmt , (case when b.row_no is not null then @w_amt := @w_amt + b.o_amt - b.b_amt - b.income_amt else '0' end) as jAmt , b.REMARKS as remarks FROM ( SELECT '0' as row_no , '000000' as sale_ym , '00000000' as SALE_DAY , 0 as SEQ , '0' as io_div , '' as PROD_CD , '' as PROD_NM , '' as CUSTS_CD , 0 as DIV_CD , 0 as fg , 0 as fg_nm , 0 as cnt , 0 as o_qty , 0 as o_amt , 0 as b_qty , 0 as b_amt , 0 as price , 0 as yul , 0 as income_amt , @w_amt := ifnull(sum(ts.O_AMT),0)-ifnull(sum(ts.OB_AMT),0)-ifnull(sum(ts.IN_AMT),0) as jan_amt , '' as REMARKS FROM STSALETB ts, (select @w_amt:=0) x WHERE ts.CUST_CD = #{paramCustCd } AND ts.SALE_DAY < #{paramPeriodFrom }
UNION ALL
SELECT t1.row_no as row_no , t1.sale_ym as sale_ym , t1.SALE_DAY as SALE_DAY , t1.SEQ as SEQ , t1.io_div as io_div , t1.PROD_CD as PROD_CD , t1.PROD_NM as PROD_NM , t1.CUSTS_CD as CUSTS_CD , t1.DIV_CD as DIV_CD , t1.fg as fg , t1.fg_nm as fg_nm , t1.cnt as cnt , sum(t1.o_qty) as o_qty , sum(t1.o_amt) as o_amt , sum(t1.b_qty) as b_qty , sum(t1.b_amt) as b_amt , t1.price as price , t1.yul as yul , sum(t1.income_amt) as income_amt , 0 as jan_amt , t1.REMARKS as REMARKS FROM TMP_WORK_TB t1 GROUP BY t1.sale_ym, t1.row_no WITH ROLLUP ) b, (select @w_amt := 0) y

SET @rownum:=0;
SELECT b.row_no as rowNum , b.sale_ym as saleYm , b.sale_day as saleDay , b.cust_cd as custCd , if(b.cust_nm='일계', b.cust_nm, cu1.cust_nm) as custNm , b.div_cd as divCd , b.fg as fg , b.fg_nm as fgNm , b.i_qty as iQty , b.ib_qty as ibQty , b.o_qty as oQty , b.oj_qty as ojQty , b.ob_qty as obQty , b.ox_qty as oxQty , b.oz_qty as ozQty , b.j_qty as jQty , b.remarks as remarks
FROM ( SELECT a.row_no , (case when a.sale_ym is not null then a.sale_ym else '총계' end) as sale_ym , (case when a.sale_day is not null then a.sale_day else '월계' end) as sale_day , a.cust_cd , (case when a.row_no is not null then a.cust_nm else '일계' end) as cust_nm , a.div_cd , a.fg , a.fg_nm , sum(a.i_qty) as i_qty , sum(a.ib_qty) as ib_qty , sum(a.o_qty) as o_qty , sum(a.oj_qty) as oj_qty , sum(a.ob_qty) as ob_qty , sum(a.ox_qty) as ox_qty , sum(a.oz_qty) as oz_qty , (case when a.row_no is not null then @w_qty := @w_qty + (sum(a.i_qty) + sum(a.ib_qty)) - (sum(a.o_qty) + sum(a.oj_qty) + sum(a.ox_qty) + sum(a.oz_qty) - sum(a.ob_qty)) else '0' end) as j_qty , a.remarks
FROM ( SELECT @rownum:=@rownum+1 as row_no , '000000' as sale_ym , '00000000' as sale_day , '0' as io_div , '' as cust_cd , '' as cust_nm , '' as div_cd , '' as fg , '' as fg_nm , 0 as i_qty , 0 as ib_qty , 0 as o_qty , 0 as oj_qty , 0 as ob_qty , 0 as ox_qty , 0 as oz_qty , @w_qty := (ifnull(sum(st.I_QTY),0)+ifnull(sum(st.IB_QTY),0))-(ifnull(sum(st.O_QTY),0)+ifnull(sum(st.OJ_QTY),0)+ifnull(sum(st.OX_QTY),0)+ifnull(sum(st.OZ_QTY),0)-ifnull(sum(st.OB_QTY),0)) as j_qty , '' as remarks FROM SSTOCKTB st, (select @w_qty:=0) x
WHERE st.PROD_CD = #{paramProdCd } AND st.STOCK_DAY < #{paramPeriodFrom }
UNION ALL
SELECT @rownum:=@rownum+1 as row_no , left(bi.BUY_DAY,6) as sale_ym , bi.BUY_DAY as sale_day , '1' as io_div , bi.CUST_CD as cust_cd , cu.CUST_NM as cust_nm , bi.DIV_CD as div_cd , bi.BUY_FG as fg , c1.COMM_NM as fg_nm , if(bi.BUY_FG = '3021101', bi.QTY, if(bi.BUY_FG = '3021103', bi.QTY, if(bi.BUY_FG = '3021104', bi.QTY, if(bi.BUY_FG = '3021105', bi.QTY, if(bi.BUY_FG = '3021106', bi.QTY, 0))))) as i_qty , if(bi.BUY_FG = '3021102', bi.QTY, 0) as ib_qty , 0 as o_qty , 0 as oj_qty , 0 as ob_qty , 0 as ox_qty , 0 as oz_qty , 0 as j_qty , bi.REMARKS as remarks FROM SBUYDTB bi
left outer join MCUSTTB as cu ON cu.CUST_CD = bi.CUST_CD left outer join MCOMMTB as c1 ON c1.COMM_CD = bi.BUY_FG
WHERE bi.PROD_CD = #{paramProdCd } AND bi.BUY_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
union all
SELECT @rownum:=@rownum+1 as row_no , left(bo.DEAL_DAY,6) as sale_ym , bo.DEAL_DAY as sale_day , '2' as io_div , bo.CUST_CD as cust_cd , cu.CUST_NM as cust_nm , bo.DIV_CD as div_cd , bo.DEAL_FG as fg , c1.COMM_NM as fg_nm , 0 as i_qty , 0 as ib_qty , if(bo.DIV_CD = '1', if(bo.DEAL_FG = '4021101', bo.QTY, if(bo.DEAL_FG = '4021102', bo.QTY, if(bo.DEAL_FG = '4021103', bo.QTY, if(bo.DEAL_FG = '4021104', bo.QTY, if(bo.DEAL_FG = '4021108', bo.QTY, if(bo.DEAL_FG = '4021109', bo.QTY, 0)))))),0) as o_qty , if(bo.DIV_CD = '1', if(bo.DEAL_FG = '4021205', bo.QTY, 0),0) as oj_qty , if(bo.DIV_CD = '9' AND bo.DEAL_FG != '4021980', bo.QTY,0) as ob_qty , if(bo.DIV_CD = '1', if(bo.DEAL_FG = '4021990', bo.QTY, 0),0) as ox_qty , if(bo.DEAL_FG = '4021980', if(bo.DIV_CD = '1', bo.QTY, 0-bo.QTY),0) as oz_qty , 0 as j_qty , bo.REMARKS as remarks FROM SDEALDTB bo
left outer join MCUSTTB as cu ON cu.CUST_CD = bo.CUST_CD left outer join MCOMMTB as c1 ON c1.COMM_CD = bo.DEAL_FG
WHERE bo.PROD_CD = #{paramProdCd } AND bo.DEAL_DAY BETWEEN #{paramPeriodFrom } AND #{paramPeriodTo }
) a, (select @w_qty:=0) y
group by a.sale_ym, a.sale_day, a.row_no with rollup
) b
left outer join MCUSTTB as cu1 ON cu1.cust_cd = b.cust_cd
where 1=1
and !(b.sale_ym = '000000' and b.sale_day = '00000000' and b.cust_nm ='일계') and !(b.sale_ym = '000000' and b.sale_day = '월계')
